@import url('owl.carousel/dist/assets/owl.carousel.css');
@import url('bootstrap/dist/css/bootstrap.min.css');
@import url('fullpage.js/dist/fullpage.min.css');
@import 'colors.scss';
@import 'predefinied.scss';




.welcome {
    position: relative;
    width: 100%;
    height: 100vh;

    &::before {
        content: '';
        background: linear-gradient(45deg, #0f4c81 35%, white 100%);
        position: absolute;
        width: 100%;
        height: 100%;
        top: 0;
        left: 0;
        z-index: 1;
        opacity: 0.5;
    }

    &__video {
        width: 100%;
        height: 100%;
        object-fit: cover;
        filter: hue-rotate(20deg);
        animation: hueRotate 6s infinite alternate-reverse cubic-bezier(.91, .11, .19, .92);
        position: absolute;
        top: 0;
        left: 0;
        z-index: 0;
    }

    &__container {
        position: absolute;
        z-index: 2;
        // font-weight: bold;
        font-family: 'Hurme';
        color: white;
        bottom: 0;
        padding: 72px;

        hr {
            background: #E0E0E2;
            height: 2px;
            border: none;
            position: absolute;
            width: 0;
            animation: hr 1.3s 1.4s cubic-bezier(0.76, 0.01, 0.21, 1.02) forwards;
        }
    }

    &__logo {
        max-width: 90%;
        width: 500px;
        height: 100%;

        &-container {
            margin-bottom: 30px;

        }

        &-arkne,
        &-group {
            path {
                fill: white;
            }
        }

        &-group {
            transforM: matrix(1.5184, 0, 0, 1.5184, -50.064, -12.644);
            animation: logoGroup 1.3s 0.4s cubic-bezier(0.76, 0.01, 0.21, 1) forwards;
        }

        &-bar {

            display: block;
            width: 500px;
            position: absolute;
            bottom: 0;
            left: 0;
            transform-origin: left;
            transform: translateX(-14px) scaleX(2);
            animation: logoBar 1.3s 0.4s cubic-bezier(0.76, 0.01, 0.21, 1) forwards;
        }
    }

    &__heading {
        color: white;
        margin-top: 60px;
        line-height: 30px;
        font-weight: normal;
        font-size: 18px;
        opacity: 0;
        margin-bottom: 30px;
        -webkit-animation: fade 1s 1.9s forwards;
        animation: fade 1s 1.9s forwards;
        text-shadow: 0 1px 1px hsl(235deg 1% 30% / 72%);
    }

    &__contact {
        opacity: 0;
        animation: fade 1s 1.9s forwards;
        -webkit-animation: fade 1s 2.9s forwards;
        text-shadow: 0 1px 1px hsl(235deg 1% 30% / 72%);
    }

    &__mail {

        &:hover,
        &:active {
            display: inline-block;
            background: linear-gradient(45deg, hsla(186, 25%, 84%, 1) 0%, hsl(200deg 90% 86%) 100%);
            background: -webkit-linear-gradient(45deg, hsla(186, 25%, 84%, 1) 0%, hsl(200deg 90% 86%) 100%);
            background-clip: text;
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
        }
    }


    &__text-gradient,
    &__mail {
        display: inline-block;
        background: linear-gradient(45deg, hsla(186, 25%, 84%, 1) 0%, hsl(200deg 90% 86%) 100%);
        background: -webkit-linear-gradient(45deg, hsla(186, 25%, 84%, 1) 0%, hsl(200deg 90% 86%) 100%);
        background-clip: text;
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
        text-shadow: none;
    }

    &__arrow {
        animation: fade 1.3s 2.7s cubic-bezier(0.76, 0.01, 0.21, 1.02) forwards;
        transition: $durationM ease;
        opacity: 0;

        &:hover {
            transform: translateY(10px);
        }
    }
}

@keyframes hueRotate {
    from {
        filter: hue-rotate(20deg);
    }

    to {
        filter: hue-rotate(-180deg);
    }
}

@media (max-width:992px) {
    .welcome__container {
        padding: 42px;
    }
}

@media (min-width:1600px) {
    .welcome__container {
        padding: 128px;
    }
}
